import React from "react";
import "../../styles/main.sass";
import { Card } from "./Card";

import uuid from "uuid";
import { Container, Media, ListGroup, ListGroupItem, Button } from "reactstrap";
import { TransitionGroup, CSSTransition } from "react-transition-group";
export class Main extends React.Component {
  state = {
    items: [
      { id: uuid(), name: "First Task" },
      { id: uuid(), name: "Second Task" },
      { id: uuid(), name: "Third Task" },
      { id: uuid(), name: "Fouth Task" }
    ]
  };
  render() {
    const { items } = this.state;
    return (
      <section className="mainWrapper">
        <Container>
          <h2>To Do</h2>
          <ListGroup>
            <TransitionGroup className="mainWrapper__cards">
              {items.map(({ id, name }) => (
                <CSSTransition key={id} timeout={500} classNames="fade">
                  <ListGroupItem>{name}</ListGroupItem>
                </CSSTransition>
              ))}
            </TransitionGroup>
          </ListGroup>
          <h2>Completed</h2>
        </Container>
      </section>
    );
  }
}
